import React, { useState, useEffect } from 'react';

const Settings = () => {
  const [settings, setSettings] = useState({
    subscribeKey: '',
    publishKey: '',
    channel: '',
    uuid: ''
  });

  useEffect(() => {
    // Load settings from localStorage on component mount
    const savedSettings = localStorage.getItem('pubnub_settings');
    if (savedSettings) {
      setSettings(JSON.parse(savedSettings));
    }
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Save to localStorage
    localStorage.setItem('pubnub_settings', JSON.stringify(settings));
    window.location.reload(); // Reload to apply new PubNub settings
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSettings(prev => ({
      ...prev,
      [name]: value
    }));
  };

  return (
    <form onSubmit={handleSubmit} className="settings-form">
      <h2>PubNub Configuration</h2>
      <div className="form-group">
        <label htmlFor="subscribeKey">Subscribe Key</label>
        <input
          type="text"
          id="subscribeKey"
          name="subscribeKey"
          value={settings.subscribeKey}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="publishKey">Publish Key</label>
        <input
          type="text"
          id="publishKey"
          name="publishKey"
          value={settings.publishKey}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="channel">Channel</label>
        <input
          type="text"
          id="channel"
          name="channel"
          value={settings.channel}
          onChange={handleChange}
          required
        />
      </div>
      <div className="form-group">
        <label htmlFor="uuid">UUID</label>
        <input
          type="text"
          id="uuid"
          name="uuid"
          value={settings.uuid}
          onChange={handleChange}
          required
        />
      </div>
      <button type="submit" className="settings-save-button">Save Settings</button>
    </form>
  );
};

export default Settings;
